(32, 'Mary M. Baker, Esq.', 'Mary M.', 'Baker', 'Kalbian Hagerty LLP', '17', 'mary trt.jpg', '', '', '', '', '<b><span style="font-size:11.0pt;line-height:115%;\nfont-family:" gothamrounded-book","serif";mso-fareast-font-family:calibri;="" mso-fareast-theme-font:minor-latin;mso-bidi-font-family:"times="" new="" roman";="" mso-bidi-theme-font:minor-bidi;color:gray;mso-themecolor:background1;="" mso-themeshade:128;mso-ansi-language:en-us;mso-fareast-language:en-us;="" mso-bidi-language:ar-sa"="">Mary Baker is a senior litigation attorney and Of\nCounsel to BerlikLaw, LLC. Mary engages primarily in civil litigation,\nincluding business and commercial litigation, employment litigation, intellectual\nproperty and trademark litigation and complex litigation</span></b>', NULL, '1', NULL, NULL, '0', NULL),
(26, 'Leah Bloomberg, Esq.', 'Leah', 'Bloomberg, Esq.', 'Sonnenschein, Sherman & Deutsch, LLP', '29', 'Leah-Bloomberg-2.jpg', '', '', '', '', '<p class="MsoNormal" style="text-align:justify"><span style="font-size:12.0pt;\nline-height:115%;font-family:" book="" antiqua","serif";mso-bidi-font-family:arial;="" color:#222222;background:white"="">Leah M. Bloomberg, Esq. is a graduate of the\nGeorge Washington University where she earned her BA in Sociology and Criminal\nJustice, and New York Law School where she earned her JD as a fellow of the\nCenter for Real Estate Studies. She is licensed to practice in both New York\nand New Jersey. Ms. Bloomberg is an associate at Sonnenschein, Sherman &amp;\nDeutsch, LLP in New York City where she specializes in real estate law, and\nmore specifically, cooperative and condominium conversion and representation.\nHer firm has converted hundreds of buildings to coops and condos over the last\n30 years and remains the leading firm in tax certorari with the NYC Department\nof Finance.&nbsp;</span><span style="font-size:12.0pt;line-height:115%;\nfont-family:" book="" antiqua","serif""=""><o:p></o:p></span></p>', NULL, '1', NULL, NULL, '', NULL),
(27, 'James R. Wrenn, Jr., Esq.', 'James R.', 'Wrenn, Jr., Esq.', 'James R. Wrenn Jr. at WrennLaw.Com', '17,21', 'Jim-Wrenn.jpg', '', '919-939-2331', 'jwrenn@wrennlaw.com', 'http://www.wrennlaw.com/', '<p class="MsoNormal" style="margin-bottom: 0.0001pt;"><b><span style="font-size: 10pt; font-family: Arial, sans-serif;">Education<o:p></o:p></span></b></p><p class="MsoNormal" style="margin-bottom: 0.0001pt;"><span style="font-size: 10pt; font-family: Arial, sans-serif;">J.D.&nbsp; - University\nof Virginia Law School (1971)<o:p></o:p></span></p><p class="MsoNormal" style="margin-bottom: 0.0001pt;"><span style="font-size: 10pt; font-family: Arial, sans-serif;">B.A. - (History)\nUniversity of Richmond (1968)<o:p></o:p></span></p><p class="MsoNormal" style="margin-bottom: 0.0001pt;"><b><span style="font-size: 10pt; font-family: Arial, sans-serif;"><o:p>&nbsp;</o:p></span></b></p><p class="MsoNormal" style="margin-bottom: 0.0001pt;"><b><span style="font-size: 10pt; font-family: Arial, sans-serif;">Bar Membership<o:p></o:p></span></b></p><p class="MsoNormal" style="margin-bottom: 0.0001pt;"><span style="font-size: 10pt; font-family: Arial, sans-serif;">Virginia State Bar:\nMember since 1972<o:p></o:p></span></p><p class="MsoNormal" style="margin-bottom: 0.0001pt;"><span style="font-size: 10pt; font-family: Arial, sans-serif;"><o:p>&nbsp;</o:p></span></p><p class="MsoNormal" style="margin-bottom: 0.0001pt;"><b><span style="font-size: 10pt; font-family: Arial, sans-serif;">Admission to Practice<o:p></o:p></span></b></p><p class="MsoNormal" style="margin-bottom: 0.0001pt;"><span style="font-size: 10pt; font-family: Arial, sans-serif;">Virginia Supreme Court\n&amp; all inferior courts of the Commonwealth of Virginia<o:p></o:p></span></p><p class="MsoNormal" style="margin-bottom: 0.0001pt;"><span style="font-size: 10pt; font-family: Arial, sans-serif;">US District Courts for\nEastern and Western Districts of Virginia<o:p></o:p></span></p><p class="MsoNormal" style="margin-bott