(38, 'Craig A. Hyldahl', 'Craig A.', 'Hyldahl', 'R.I.C.H. Planning Group, LLC', '3,18', 'Craig-Hyldalh.jpg', '', '', '', '', '<p class="MsoNormal" style="margin-bottom: 0.0001pt; text-align: justify;"><span class="apple-converted-space"><b><span style="font-family:" gothamrounded-book","serif";color:#7f7f7f;mso-themecolor:="" text1;mso-themetint:128"="">Craig A. Hyldahl, CFP® CDFA™&nbsp;</span></b></span><b><span ";="" "=""><span style="font-family:" gothamrounded-book","serif";="" color:#7f7f7f;mso-themecolor:text1;mso-themetint:128"="">financial services\nindustry veteran with over 33 years in the business, focusing on the financial\nplanning needs of divorcing couples, businesses and successful\nindividuals.&nbsp; &nbsp;With the CDFA™ designation, Craig helps divorcing\ncouples determine the short term and long term financial impact of any proposed\ndivorce settlement. He also provides valuable information on financial issues\nthat are related to divorce, such as tax consequences, pension plan division,\ncontinued health care coverage, and stock options.</span></span></b><span style="font-family:" gothamrounded-book","serif";color:#7f7f7f;mso-themecolor:="" text1;mso-themetint:128"=""><o:p></o:p></span></p>', NULL, '1', NULL, NULL, '', 'https://s3.amazonaws.com/cdnuploads.trtcle/Craig-Hyldalh.jpg'),
(39, 'Paul Stuart Haberman, Esq.', 'Paul Stuart', 'Haberman, Esq.', 'Goldberg Segalla, LLP', '34', 'Paul-Haberman-2.jpg', '', '', '', '', '<p class="MsoNormal" style="margin-bottom: 0.0001pt; text-align: justify;"><b><span style="font-family:" gothamrounded-book","serif";mso-bidi-font-family:arial;="" color:gray;mso-themecolor:background1;mso-themeshade:128;"="">Paul\nStuart Haberman, Esq. is a New York and New Jersey-based defense-side civil\nlitigator.&nbsp; Mr. Haberman is also a licensed boxing manager, an active\nparticipant in his local government, and an accomplished writer with several\npublished articles and an online column on legal issues in boxing</span></b><span style="font-family:" gothamrounded-book","serif";mso-bidi-font-family:arial;="" color:gray;mso-themecolor:background1;mso-themeshade:128;background:white"="">. <b>e is the author of nearly 35 successful\ndispositive motions in both state and federal court. &nbsp;</b></span><span style="font-family:" gothamrounded-book","serif";color:gray;mso-themecolor:background1;="" mso-themeshade:128"=""><o:p></o:p></span></p>', NULL, '1', NULL, NULL, '', NULL),
(29, 'Allan H. Goodman, J.D.', 'Allan H.', 'Goodman', 'Allan H. Goodman, J.D.', '3', 'a trt.jpg', '', '', 'book@solomonpublications.com', '', '<p class="MsoNormal" style="margin-bottom: 0.0001pt; text-align: justify;"><b><span style="font-family:" gothamrounded-book","serif";="" color:gray;mso-themecolor:background1;mso-themeshade:128"="">Mr. Goodman left the\nprivate practice of law when he was appointed as a Judge on the United States\nGeneral Services Administration Board of Contract Appeals, where he adjudicated\nclaims between private contractors and the federal government, serving as both\na trial judge and a mediator. Many of these disputes involve complex\nconstruction projects, such as federal office buildings, nuclear waste clean-up\nsites, and highway construction. He successfully mediated the largest claim\narising from the Big Dig Project in Boston, MA.</span></b><span style="font-family:" gothamrounded-book","serif";color:gray;mso-themecolor:background1;="" mso-themeshade:128"=""><o:p></o:p></span></p>', NULL, '1', NULL, NULL, '0', NULL),
(35, 'Coleen F. Middleton, Esq.', 'Coleen F.', 'Middleton, Esq.', 'Cohen Tauber Spievack & Wagner, P.C.', '11', 'Coleen-Middleton.jpg', '', '', '', '', '<p class="MsoNormal" style="margin-bottom: 0.0001pt; text-align: justify;"><b><span style="font-family:" gothamrounded-book","serif";color:gray;mso-themecolor:background1;="" mso-themeshade:128"="">Coleen specializes in the areas of white-collar criminal\ndefense, government investigations and complex civil litigation.&nbsp; She\nserved as an Assistant United States Attorney for the District of New Jersey,\nwhere she specialized in the prosecution of white-collar crime.&nbsp; She was\nalso First Associate Commissioner of the New York City Department of\nInvestigation, and an Assistant Deputy Mayor of Philadelphia.</span><o:p></o:p></b></p>', NULL, '1', NULL, NULL, '', NULL),
